pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";

contract Rate is Ownable {
  modifier onlyDeployer() {
    require(msg.sender == deployer, "Not Allowed");
    _;
  }

  //public adddress variable to store the address of the deployer
  address public deployer;

  //public variable to store the rate of Interest for the Loan
  uint32 public rateOfInterest; // <=3 digits

  //public variable to store the Loan to Value Ration
  uint32 public s_LTV; //<=3 digits

  //public variable to store the Debt to Income Ratio
  uint64 public DTI; //<=3 digits

  uint128 public totalPreApprovedLoans; // 100000000000000

  uint128 public totalLoansDisbursed; //100000000000000

  uint256 public totalAllowableFunds;

  // uint256 public approvedButNotDisbursedLoans; //can be calculated

  // uint256 public totalAvailableFunds; //can be calculated

  // Emitted when the rateOfInterest changes
  event RateChanged(uint32 _newrateOfInterest, uint32 oldrateOfInterest);

  //Emitted when the LTI is changed
  event LTVChanged(uint32 newLTI, uint32 oldLTI);

  // Emitted when the DTI changes
  event DTIChanged(uint64 newDTI, uint64 oldDTI);

  // Emitted when the deployer address changes
  event DeployerChanged(address indexed previousDeployer, address indexed newDeployer);

  constructor(address _owner) {
    //sets the given address as the deployer of the contract.
    deployer = _owner;
  }

  //Function to change the deployer address in the contract.
  function changeDeployer(address _newDeployer) public onlyDeployer {
    address _prevDeployer = deployer;
    deployer = _newDeployer;

    emit DeployerChanged(_prevDeployer, _newDeployer);
  }

  // Stores a new rateOfInterest in the contract, can be only triggered by the DAO
  function updateRate(uint32 _newrateOfInterest) public onlyOwner {
    uint32 val = rateOfInterest;
    rateOfInterest = _newrateOfInterest;

    emit RateChanged(_newrateOfInterest, val);
    delete val;
  }

  // Reads the last stored rateOfInterest
  function retrieveRate() public view returns (uint32) {
    return rateOfInterest;
  }

  //Functoin to update the loan to Value Ratio, it can only be triggered by the DAO.
  function updateLTV(uint32 _newLTV) public onlyOwner {
    uint32 _prevLTV = s_LTV;
    s_LTV = _newLTV;

    emit LTVChanged(_prevLTV, _newLTV);
    delete _prevLTV;
  }

  //Function that reads and returns the value of LTV
  function retrieveLTV() public view returns (uint32) {
    return s_LTV;
  }

  //Function that updates the value of Debt to Income Ratio, it can be only triggered by the DAO.
  function updateDTI(uint64 _newDTI) public onlyOwner {
    uint64 _prevDTI = DTI;
    DTI = _newDTI;

    emit DTIChanged(_prevDTI, _newDTI);
    delete _prevDTI;
  }

  //Function to retrieve the value of DTI
  function retrieveDTI() public view returns (uint64) {
    return DTI;
  }

  function updateTotalAllowableFunds(uint256 _newTotalAllowableFunds) public onlyDeployer {
    totalAllowableFunds = _newTotalAllowableFunds;
  }

  function updateTotalLoansDisbursed(uint128 _newTotalLoansDisbursed) public onlyDeployer {
    totalLoansDisbursed = _newTotalLoansDisbursed;
  }

  function updateTotalPreApprovedLoans(uint128 _newTotalPreApprovedLoans) public onlyDeployer {
    totalPreApprovedLoans = _newTotalPreApprovedLoans;
  }

  function retrieveTotalAllowableFunds() public view returns (uint256) {
    return totalAllowableFunds;
  }

  function retrieveTotalLoansDisbursed() public view returns (uint128) {
    return totalLoansDisbursed;
  }

  function retrieveTotalPreApprovedLoans() public view returns (uint128) {
    return totalPreApprovedLoans;
  }
}
